section.example:
// Get the object for the AudioPlayer Control:
var audioObj = {dialog.object}.getControl('AUDIOPLAYER');

if (audioObj) { // if the object exists
    // get the state info for the audio player
    var audioStateObj = audioObj._play.getState();

    // print the audio object's state JSON in an alert
    alert(JSON.stringify(audioStateObj));
}
