section.example:
function showAlias() {
    var alias = {dialog.object}.dialogId;
    var message = "The Alias of the UX is " + alias;
       
    alert(message);
}

function showAlias(ux) {
    var alias = ux.dialogId;
    var message = "The Alias of the UX is " + alias;
       
    alert(message);
}

{dialog.object}._functions.showAlias = function () {
    var alias = {dialog.object}.dialogId;
    var message = "The Alias of the UX is " + alias;
       
    alert(message);
}
