 function processCompany(data,temp,root) {
    //limit to first two items in the array
    data = data.slice(0,2);
    return data;
}

 function processCompany(data,temp,root) {
    //sort on the 'firstname' property
    data.sort( function(a,b) {
        if (a.firstname < b.firstname) return -1;
        if (a.firstname > b.firstname) return 1;
        return 0;
        }
    )

    //limit to first two items in the array
    data = data.slice(0,2);
    return data;
}

