 A5.u.template.formats.formatName = function(value,flag) {
    if(flag == 'u') return value.toUpperCase();
    else if(flag == 'l') return value.toLowerCase();
    else return value;
}

A5.u.template.formats.phoneNumber = function(value) {
    return value.toFormat('=(###)-###-#####');
}
