                        function myjson as c(e as p)
    myjson = <<%json%
    {
        "schema" : { 
            "firstname" : { "type" : "C" , "width": 30 }, 
            "lastname" : { "type" : "C" , "width": 30} , 
            "company" : { "type" : "C" , "width": 30} , 
            "age" : { "type" : "N" , "width" : 3, "decimal" : 0 }
        },
        "data": [
            {"firstname":"Fred", "lastname":"Smith", "company" : "Alpha Software", "age" : 30},
            {"firstname":"John", "lastname":"Jones", "company" : "Beta Software", "age" : 23}
        ]
    }
%json%
end function

function myjson as c(e as p)
    myjson = <<%json%
    {
        "schema": {
            "name": {
                "type": "c",
                "width": 30
            },
            "companyName": {
            "source": "company",
            "type" : "c",
            "width" : 30
            },

            "street": {
                "source": "address.street",
                "type": "c",
                "width": 40
            },
            "number": {
                "source": "address.number",
                "type": "n",
                "width": 5,
                "decimal": 0
            }
        },
        "data": [
            {
                "name": "Fred Smith",
                "company" : "XYZ Corp",
                "address": {
                    "street": "Main Street",
                    "number": 23
                }
            },
            {
                "name": "John Jones",
                "company" : "ABC Corp",
                "address": {
                    "street": "Corner Road",
                    "number": 3
                }
            }
        ]
    }
    %json%
end function

function myjson as c(e as p)
    myjson = <<%json%
    {
    "schema" : {
