Last Name:|[lname];
<OK> <%B=T;O=Owner Draw Button%tb>
{pane=Two}
{endtab};

{lf};
{wrap=80};
{defaulttabstops}
{tab=x3}
{pane=One}
First Name:|[fname];
Last Name:|[lname];
<OK> <%B=T;O=Owner Draw Button%tb>
{pane=Two}
{endtab};
%dlg%)
