section.example:
ui_modeless_dlg_box("Dialog1",<<%dlg%
{position=1,1}
{can_exit=close}
{wrap=150}
{lf};
Variable X [.20x] <Set Variable!setVar>;
<Close>;
%dlg%,<<%code%
if a_dlg_button = "Close" then
        a_dlg_button = ""
        ui_modeless_dlg_close("Dialog1")
else if a_dlg_button = "SetVar" then
        a_dlg_button = ""
    x = "Dialog1"
end if
%code%)

ui_modeless_dlg_box("Dialog2",<<%dlg%
{can_exit=close}
{position=4,4}
{linked=Dialog1}
{wrap=150}
{lf};
Variable X [.20x] <Set Variable!setVar>;
<Close>;
%dlg%,<<%code%
if a_dlg_button = "close" then
        a_dlg_button = ""
        ui_modeless_dlg_close("Dialog2")
else if a_dlg_button = "Setvar" then
        a_dlg_button = ""
    x = "Dialog2"
end if
%code%)
