section.example:
dim dlg_title as C
dlg_title = "Advance on Enter"
dim fn as C
dim ln as C
dim address1 as C
dim address2 as C
dim city as C
dim st as C
dim zip as C
ui_dlg_box(dlg_title,<<%dlg%
{on_key=enter}
Press Enter to advance to next field. When focus is on OK button, Enter will close the dialog;
{lf};
{frame=1,1}
{region}
First name: | [.50fn];
Last name: | [.50ln];
Address 1: | [.50address1];
Address 2: | [.50address2];
City: | [.50city];
State: | [.5st];
Zip: | [.10zip];
{endregion};
{lf};
{justify=right}
