section.example:
// ViewBox ID is 'StepIndicator':
var val = Number({dialog.object}.getValue('StepIndicator'));
val = Math.min(val+1,3);
{dialog.object}.setValue('StepIndicator',val);

// ViewBox ID is 'StepIndicator':
var val = Number({dialog.object}.getValue('StepIndicator'));
val = Math.max(val-1,0);
{dialog.object}.setValue('StepIndicator',val);
