function myOnSyncHandler as v (e as p) 

e.javascript = <<%txt%
var lObj =  DLG1_DlgObj.getControl('LIST1');
lObj._cleanMediaFiles = lObj._harvestMediaFields({cleanOnly: true}); //only needed in a PhoneGap application if you have media (images, videos, audio) fields
lObj.setRowClean([3]);
lObj.setRowClean([4]);
lObj.setServerSideError(true,[5],[
    {
        errorText: 'State cannot be blank.',
        varName: 'State'
    }
]);
DLG1_DlgObj.refreshClientSideComputations();
lObj._pushMediaFieldArrayToList(lObj._cleanMediaFiles,true); //only needed in a PhoneGap application if you have media (images, videos, audio) fields
delete lObj._cleanMediaFiles; //only needed in a PhoneGap application if you have media (images, videos, audio) fields
lObj._populateAllChildLists(); 
lObj._setRoute(r);
lObj._fetchMediaFiles(-1,r); //only needed in a PhoneGap application if you have media (images, videos, audio) fields
lObj._setRoute(r);
%txt%

end function
