function populateNode() {
var t = {dialog.object}.getControl('tree1');

var _dc = [
{ html: 'sub dynamic 1'},
{ html: 'sub dynamic_2', onClick: function() { alert('you clicked on sub-dynamic_2'); }}

]

var _d = [
{ html: 'dynamic'},
{ html: 'dynamic_2', children: _dc }

]

t.populateNode([1], _d);

}

var t = {dialog.object}.getControl('tree1');
var _d = t.data;
_d.push({ html: 'new mode'});
t.populate(_d)
