section.example:
var vb = {dialog.object}.getControl('MYVIEWBOX1');
var data = {name: 'Fred Smith', age: 23};
vb.populate(data);
vb.refresh();

var vb = {dialog.object}.getControl('MYVIEWBOX1');
var obj = {name: 'Fred Smith', age: 23};
vb.setState(obj);
vb.refresh();

var v = {dialog.object}.getControl('v1'); //get pointer to the viewbox
v.setElementDisabled('#button1',true);   
//if the 'item1' items specifies a disabled classname,
//it would be applied to the button.
