section.example:
var ele = $('APPCACHE_DOWNLOAD_PROGRESS');
ele.style.display = '';

if(typeof e.event.total != 'undefined') { 
    ele.innerHTML = 'Loaded ' + e.event.loaded + ' of ' + e.event.total;
    if(e.event.loaded == e.event.total) { 
        ele.style.display = 'none';
    }
} else { 
    ele.innerHTML = 'Working....';
}
