function jsData() {
    var a = [];
    a.push({sales: 23, region: 'east'});
    a.push({sales: 13, region: 'west'});
    a.push({sales: 3, region: 'south'});
    a.push({sales: 9, region: 'north'});

    return a;
}

function jsData() {
    var a;
    a = "21|east" + "\n";
    a += "13|west" + "\n";
    a += "3|south" + "\n";
    a += "9|north";

    return a;
}
