var rowNum = {grid.object}._selectedRow;
{grid.Object}.globalUpdate(rowNum,{
	fieldToUpdate: '',
	elementIdSearchValue: '',
	elementIdReplaceValue: '',
	rowsToUpdate: 'AllVisibleRows',
	promptForConfirmation: false,
	confirmationMessage: 'OK to change value in row [row] from \'[old]\' to \'[new]\'?',
	allInstances: true,
	caseInsensitive: true,
	type: 'search'
});
