section.example:
dim pj as p
pj.text = "some text with ' single quote"
?json_generate(pj)
= {
    "text": "some text with ' single quote"
}

dim p as p
p.name = "foo"
p.function = "{javascript}function() { alert('hello') }"

?json_generate(p,.t.)

= {
	"name": "foo",
	"function": function() { alert('hello') }
}
