dim scriptJson as c = <<%json%
[ { "statement" : "dim"  ,"name" : "x", "type" : "c" }
 , { "statement" : "assign"  , "leftexpression" : { "token" : "variable" , "name" : "x" } , "rightexpression" : { "token" : "constant" ,"value" : "string"  } }
 ]
%json%

? xbasic_from_json(scriptJson)
= dim x as c
x = "string"
