// To get a pointer to the A5.ViewBox class see {dialog.object}.getControl
// assume vbObj is a pointer to an instance of the A5.ViewBox class
// assume oldDate is a date - and the view box should be disabled if less then on minute has passed
var date = new Date();
if(date.now()-oldDate.now() > (1000*60)){
	vbObj.setDisabled(false);
} else{
	vbObj.setDisabled(true);
}
