page.example:
// assume cEle is an element into which the button list wants to be added
var html = A5.buttonLists.html('color',{theme: 'Alpha'},[
	{html: 'Red', value: 'red'},
	{html: 'Green', value: 'green'},
	{html: 'Blue', value: 'blue'}
],'red','a5-item="color" a5-value="{value}"');
cEle.innerHTML = html;
