page.example:
var data = [
	{l: 'A', v: 1.4},
	{l: 'B', v: .6},
	{l: 'C', v: 1.5},
	{l: 'D', v: 1},
	{l: 'E', v: 1.2},
	{l: 'F', v: 1}
];
var s = {
	input: {
		map: {
			dimensions: [
				['value',{
					src: 'v',
					as: 'number',
					stack: 'percent',
					base: 0
				}],
				['label',{
					src: 'l',
					as: 'string'
				}]
			]
		}
	},
	output: {
		render: {
			type: 'radial',
			width: 200,
			height: 200,
			arc: {margin: 20},
			stretch: true,
			offset: {bottom: 40, top: 40, left: 40, right: 40},
			layer: {
				value: {
					marks: {
						show: 'all',
						labels: {
							location: 'none',
							style: 'font-size: 75%; font-weight: bold;'
						}
					},
					title: {
						location: 'after',
						html: 'Score',
						offset: {major: 20}
					}
				},
				point: {
					type: 'bar',
					label: {
						location: 'callout',
						style: 'font-size: 75%; font-weight: bold;',
						offset: {axis: 2},
						callout: {type: 'step', from: 'center', line: {width: 1}},
						html: '{point.src.items[0].l}'
					}
				},
				annotations: [
					{
						type: 'legend',
						label: {
							html: '{group.dimensions.label.values[0]}'
						}
					}
				]
			}
		}
	}
}
var chartHTML = A5.chart.generate(data,s);
