page.example:
var data = {
		firstname: 'Bob',
		lastname: 'Thomas',
		work: {
			address: '123 Main St.',
			city: 'Burlington',
			state: 'MA'
		},
		home: {
			address: '456 Maple St.',
			city: 'Burlington'
		}
	};
var welcomeStr = A5.u.template.expand(data,'Hello {firstname} {lastname}!');
// welcomeStr = 'Hello Bob Thomas!'
var profileStr = A5.u.template.expand(data,{
		template: '{fristname} {lastname}\n\nWork Address:\n{*partial address}\n\nHome Address:\n{*partial address}',
		nullValue: '[Missing]',
		partials: {
			address: '{address}, {city} {state}'
		}
	});
// profileStr =	'Bob Thomas
//		Work Address:
//		123 Main St., Burlington MA
//		
//		Home Address:
//		456 Maple St., Burlington [Missing]'
