 var onDeleteSuccess = function() { 
    alert("File was deleted");
};
var onError = function(e) { 
    alert("An error occured when deleting the file!");
};
var filepath = {dialog.object}.phoneGapGetLocalDirURL('private') + 'file.txt';
{dialog.object}.phoneGapDeleteFileURL(filepath, onDeleteSuccess, onError);
