var buttonListObj = {dialog.object}.getControl('BUTTONLIST_1');

if (buttonListObj) {
    var buttons = [
        {html: 'New Button 1', value: 'nb1', tip: 'Help for new button 1'},
        {html: 'New Button 2', value: 'nb2', tip: 'Help for new button 2'},
    ];

    buttonListObj.populate(buttons);
}
