page.example:
//get a pointer to the map control for variable 'mymap1'
var mapObj = {dialog.object}.getControl('mymap1');

if (mapObj) {
    // center on a lat/lng
    mapObj.setCenter([42.1,-71.3],true);

    // center on an address
    mapObj.setCenter('70 Blanchard Road Burlington, MA');
}
