page.example:
var onResult = function(properties) { 
    if (properties == false) {
        alert("File does not exist.");
    } else {
        alert("File exists!");
    }
};
var onError = function(e) { 
    alert("An error occured!");
};
var filepath = {dialog.object}.phoneGapGetLocalDirURL('private') + 'file.txt';
{dialog.object}.phoneGapFileExistsURL(filepath, onResult, onError);
