section.example:
dim lv as P
dim pe as P
lv = local_variables()
people=<<%str%
selwyn
richard
cian
tim
aaron
%str%
ui_dlg_box("{BEGINEMBEDDED}",<<%dlg%
{stretch=width}
[.40,10name^#people]
{hslider}
{beginembedded}
{stickto=left}
Name|{stretch=width}[.20Name];
{stickto=left}Company|{stretch=width}[.30Company];
{stickto=left}Address|{stretch=width}[.30Address1];
|[.30Address2];
|[.15City]{stickto=right}[.3Atate][.9ZIP];
{stickto=left}
{endembedded};
{stretch=width}
{line=1,0};
{stickto=left,bottom}

%dlg%,<<%code%
if a_dlg_button = "Inside" then
    pe = *for_each(i,i+" = "+eval(i),properties_enum(lv))
    a_dlg_button = ""
    ui_msg_box("event",pe)
    delete pe
end if
%code%)
