section.example:
function bar as V ( msg as C )
    ui_msg_box("bar", msg )
end function
dim fn as F
fn = bar
fn("hello")

function foo as v ( func as F )
    func("Hello")
end function
function bar as V ( msg as C )
    ui_msg_box("bar", msg )
end function
function blah as V ( msg as C )
    ui_msg_Box("blah", msg)
end function
foo(bar)
foo(blah)
