page.example:
		dim json as c 
json = <<%txt%
{firstname: 'Fred', lastname: 'Smith', Address: {
Street: '123 Main St', 
City: 'Boston', 
State: 'Ma'
}
}
%txt%

?json_reformat_safe(json)
= {
    "firstname": "Fred",
    "lastname": "Smith",
    "Address": {
        "Street": "123 Main St",
        "City": "Boston",
        "State": "Ma"
    }
}
