var request = require('request')
var path = 'https://alphademo1-transform.cloud.alphasoftware.com/transformAPIVersion1.a5svc/createNewUserAccount';
var data = {"userid": "value_of_userid","password": "value_of_password","roles": "value_of_roles","displayname": "value_of_displayname"};
var dataJSON = JSON.stringify(data)
request.post(
	{
		headers: {apikey: "Your_api_key"},   
		url: path, 
		form: dataJSON}, 
		function(err,httpResponse,body){
			if(err) console.log(err)
			console.log(body)
	}
)

dim apikey as c = "Your_api_key" dim postdata as c postdata = <<%txt% { "userid": "value_of_userid", "password": "value_of_password", "roles": "value_of_roles", "displayname": "value_of_displayname" }
%txt%
dim url as c = "/createNewUserAccount" dim pr as p pr = a5dialogHelper_transformAPIQuery(url,postdata,apikey) if pr.error = .f. then dim result as c result = pr.result else dim errorText as c errorText = pr.errorText end if
