var https = require('https');
var options = {
  protocol: "https:",
  hostname: "alphademo1-transform.cloud.alphasoftware.com",
  path: "/transformAPIVersion1.a5svc/GetFormInstancesArrayForFormId/value_of_formId?paginated=value%5fof%5fpaginated&pagesize=value%5fof%5fpagesize&pageNumber=value%5fof%5fpageNumber&mode=value%5fof%5fmode&resolveMediaFields=value%5fof%5fresolveMediaFields&getRecordCount=value%5fof%5fgetRecordCount&timestamp=value%5fof%5ftimestamp&fieldList=value%5fof%5ffieldList",
  headers: {apikey: "Your_api_key"}
};

https.get(options, (resp) => {
  let data = '';
  resp.on('data', (chunk) => {data += chunk;});
  resp.on('end', () => {console.log(data);});
}).on("error", (err) => {console.log("Error: " + err.message);});
