section.example:
var request = require('request')
var path = 'https://transform.alphasoftware.com/transformAPIVersion1.a5svc/inviteUsers/';
var data = {"usersToInvite": "value_of_usersToInvite"};
var dataJSON = JSON.stringify(data)
request.post(
	{
		headers: {apikey: "Your_api_key"},   
		url: path, 
		form: dataJSON}, 
		function(err,httpResponse,body){
			if(err) console.log(err)
			console.log(body)
	}
)

dim apikey as c = "Your_api_key" dim postdata as c postdata = <<%txt% { "usersToInvite": "value_of_usersToInvite" }
%txt%
dim url as c = "/inviteUsers/" dim pr as p pr = a5dialogHelper_transformAPIQuery(url,postdata,apikey) if pr.error = .f. then dim result as c result = pr.result else dim errorText as c errorText = pr.errorText end if
